mapper.select:
        SELECT
			mcl.client_id clientId,
			mcl.client_name clientName,
			mcl.client_num clientNum,
			mcl.site,
			mcl.client_type clientType,
			mcl.card_num cardNum,
			mcl.create_date createDate,
			mcl.update_date updateDate
			FROM
			m_client AS mcl
			
			WHERE
			1=1
			and mcl.client_name =  #{clientName} 
	        and mcl.client_num = #{clientNum } 
	        and mcl.site = #{site		} 
	        and mcl.client_type = #{clientType} 
	        and mcl.card_num = #{cardNum	} 
	        and mcl.create_date = #{updateDate} 
	        and mcl.update_date = #{updateDate}
